SELECT a.*, b.account_name AS "accountName", c.realm_name AS "realmName", c.realm_zone_name AS "realmZone", c.realm_type AS "realmType", d.dict_label AS "characterClassName" FROM nuanxin_character a LEFT JOIN nuanxin_account b ON a.character_account = b.account_id LEFT JOIN nuanxin_realm_list c ON a.character_realm = c.realm_id LEFT JOIN js_sys_dict_data d ON a.character_class = d.dict_value AND d.dict_type = 'nx_character_class'
${sqlMap.where.toSql()}
AND c.realm_type = #{realmType}

SELECT a.*, b.account_name AS "accountName", c.realm_name AS "realmName", c.realm_zone_name AS "realmZone", c.realm_type AS "realmType", d.dict_label AS "characterClassName" FROM nuanxin_character a LEFT JOIN nuanxin_account b ON a.character_account = b.account_id LEFT JOIN nuanxin_realm_list c ON a.character_realm = c.realm_id LEFT JOIN js_sys_dict_data d ON a.character_class = d.dict_value AND d.dict_type = 'nx_character_class'
character_id = #{characterId}
LIMIT 1
